import React, { Component } from 'react'
import { Link } from 'react-router-dom';

export default class Login extends Component {
  constructor(){
    super()
    this.state={
      email:'',password:''
    }
  }
  render() {
    return (
      <div>
        <div className="row d-flex justify-content-center mt-5  text-center ">
  

            <form className='border p-2 rounded' >
            <h4 className='text-center'>Login Form</h4>
            <div className="form-outline my-4 ">
                <input type="email/name" id="loginName" name="email" value={this.state.email} className="form-control" />
                <label className="form-label" >Email or username</label>
            </div>

            <div className="form-outline mb-4">
                <input type="password" id="loginPassword" name='password' value={this.state.password} className="form-control" />
                <label className="form-label" >Password</label>
            </div>

            <div className="row mb-4">
                <div className="col-md-6 d-flex justify-content-center">
                
                </div>
                <div className="col-md-6 d-flex justify-content-center">
                <Link to="/forgetpass" >Forgot password?</Link>
                </div>
            </div>
            
            <button type="submit" onClick={this.login}  className="btn btn-primary btn-block mb-4">Sign in</button>

            <div className="text-center">
                <p>Not a member?<Link to="/signup" >Signup</Link></p>
            </div>
            </form>
        </div>
      </div>
    )
  }

  login(event){
    event.preventDefault();
    this.setState( {
      email:event.target.elements.email.value,
      password:event.target.elements.password.value

    }
        
    )
    console.log(this.state)
  }


}
